import java.io.* 
import jxl.* 
import jxl.write.* 
import java.text.SimpleDateFormat
import groovy.json.JsonSlurper
import groovy.json.JsonOutput

def accessToken = "fdc76ca650b28f12710426baaae93a637014c4bd";


def get = new URL("http://rest-api.upskills.in/api/rest/cart").openConnection();
get.setRequestProperty("Authorization", "Bearer "+ accessToken)

def postRC = get.getResponseCode();
def responseText = get.getInputStream().getText()
log.info (responseText)
def slurper = new JsonSlurper()
def content = slurper.parseText(responseText)

log.info(content.data.products.key);
log.info(content.data.products.product_id);

def product_key_list =[]
def product_id_list = []
product_key_list = content.data.products.key
product_id_list = content.data.products.product_id

log.info(product_key_list)
log.info(product_id_list)

get.disconnect()

